from __future__ import print_function
from __future__ import absolute_import

from .telnetComm import *
import telnetlib

"""
it's a derived class , that inherits telnetComm and override telnet object
"""
class telnetCommOrigin(telnetComm):

    def __init__(self, host=None, port=DEFAULT_TELNET_PORT, timeout=5, uname=None, password=None, **extraParameters):
        super().__init__(host=host, port=port, timeout=timeout, uname=uname, password=password, **extraParameters)

    """
    There was an issue where this class is used and overrides the telnetlib function (see telnetLibWrapper.py).
     We need the ability to use the original telnetLib (see telnetCommOrigin.py)
    """

    def _get_telnet_object(self,host, port, time_out_retries):
        return telnetlib.Telnet(self.m_host, self.m_port, time_out_retries)
