from enum import Enum, IntEnum


class I2CWrapperType(Enum):
    I2CWrapper = 0,
    MockI2CWrapper = 1


class I2CProviderType(Enum):
    Sub20 = 'Sub20',
    Apollo = 'Apollo',
    SUB20 = 'SUB20',
    MIBS = 'MIBS'


class I2CMode(IntEnum):
    I2C_SLOW_MODE = 0,
    I2C_NORMAL_MODE = 1


class I2cInterfaceEnum(IntEnum):
    I2C_LPT = 0,
    I2C_USB = 1,
    I2C_CPU = 2


class BoardPowerModeEnum(IntEnum):
    POWER_ON = 0,
    POWER_OFF = 1,
    POWER_LOCAL = 2


class TypeEnum(IntEnum):
    MAX1139 = 0
    MAX1239 = 1


class VrefEnum(IntEnum):
    INTERNAL = 0,
    EXTERNAL = 1
