import sys
from Marvell.EMClientPy.src.Common.Constants import *


def GetInterpreterType():
    if sys.version.__contains__("64 bit") or sys.maxsize > 2**32:
        return InterpreterType._64BIT
    return InterpreterType._32BIT


def TwosComplement(bits, data):
    if bits < 32:
        mask = 0x0
        for i in range(bits - 1):
            mask |= (0x1 << i)

        retVal = data & mask

        msb = (0x1 << (bits - 1)) & data
        retVal -= msb

        return retVal

    else:  # bits == 32
        return int(data)


def uintCast(num: int):
    return num & 0xFFFFFFFF


def celsiusToKelvin(temp: float) -> float:
    return temp + 273.15


def kelvinToCelsius(temp: float) -> float:
    return temp - 273.15