from .ElectricalManagmentLayer.ElectricalManager import ElectricalManager
from .ElectricalManagmentLayer.ElectricalManagerMock import ElectricalManagerMock


class EMClientFactory(object):
    @staticmethod
    def getEM_Instance(isMock: bool = False):
        if isMock:
            m_em = ElectricalManagerMock()
        else:
            m_em = ElectricalManager()
        return m_em
