from Marvell.EMClientPy.src.ElectricalManagmentModels.Base_Models import *
from .Ac2DcPS import Ac2DcPS
from .Mux import Mux
from .ClkSync import ClkSync
from .PS import PS
from .IO import IO
from .A2D import A2D
from .SMM import SMM
from .TSEN import TSEN
from .PLD import PLD


class ItemFactory(object):
    @staticmethod
    def create(elm, parent, connId) -> ItemType:
        elmType = ItemType.getElmType(elm)
        if elmType == ItemTypeEnum.MUX:
            return Mux(elm, parent, connId, ItemFactory)
        elif elmType == ItemTypeEnum.PS:
            return PS(elm, parent, connId)
        elif elmType == ItemTypeEnum.IO:
            return IO(elm, connId)
        elif elmType == ItemTypeEnum.A2D:
            return A2D(elm, parent, connId)
        elif elmType == ItemTypeEnum.SMM:
            return SMM(elm, parent, connId)
        elif elmType == ItemTypeEnum.TSEN:
            return TSEN(elm, parent, connId)
        elif elmType == ItemTypeEnum.PLD:
            return PLD(elm, parent, connId)
        elif elmType == ItemTypeEnum.ClkSync:
            return ClkSync(elm, parent, connId)
        elif elmType == ItemTypeEnum.Ac2DcPS:
            return Ac2DcPS(elm, parent, connId)
        else:
            raise TypeError('The element is of unknown ItemType')