from enum import Enum, IntEnum


class ItemTypeEnum(Enum):
    MUX = 'mux'
    IO = 'io'
    PS = 'ps'
    A2D = 'a2d'
    SMM = 'smm'
    TSEN = 'tsen'
    PLD = 'pld'
    ClkSync = 'clkSync'
    Ac2DcPS ='ac2dcPs'
    NA = 'N/A'


class MeasureTypeEnum(Enum):
    VOLT = 'volt'
    AMP = 'amp'
    mV = 'mV'
    mA = 'mA'


class MarginSelectionEnum(Enum):
    HIGH = 'high'
    LOW = 'low'


class PolarityTypeEnum(Enum):
    REGULAR = 'regular'
    INVERSE = 'inverse'


class IOFuncTypeEnum(Enum):
    FuncError = 'FuncError'
    BoardReset = 'BoardReset'
    DutPwr = 'DutPwr'
    BoardPwr = 'BoardPwr'
    GePhyReset = 'GePhyReset'
    ResetDUT = 'ResetDUT'
    BoardPowerStatus = 'BoardPowerStatus'
    StartControl = 'StartControl'
    RdyStatus = 'RdyStatus'
    FanPwr = 'FanPwr'
    SetVoltageControl = 'SetVoltageControl'


class MuxTypeEnum(Enum):
    PCA9544 = 'PCA9544'
    PCA9548 = 'PCA9548'


class ModeEnum(IntEnum):
    SINGLE_ENDED = 0
    DIFFERENTIAL = 1


class PolarityEnum(IntEnum):
    BIPOLAR = 0
    UNIPOLAR = 1


class PLDTypeEnum(Enum):
    TC1 = 'TC_1'


class ClkSyncTypeEnum(Enum):
    LMK05318 = 'LMK05318'


class ClkSyncBaseRegisterType(Enum):
    PLL1_NUM = 0x6e  # R110 holds most significant bit and R114 the least significant bit
    PLL2_NUM = 0x88  # R136 holds most significant bit and R138 the least significant bit
    PPL2_DEN = 0x4d  # R333 holds most significant bit and R335 the least significant bit
