from Marvell.EMClientPy.src.Common.Types import *
from .I2CWrapper import I2CWrapper
# from .ApolloProvider import ApolloProvider


class I2CWrapperFactory(object):
    """Factory to create I2C provider Sub20 / Apollo"""

    @staticmethod
    def Create(i2cWrapperType: I2CWrapperType):
        if i2cWrapperType.value == I2CWrapperType.I2CWrapper.value:
            return I2CWrapper()
        elif i2cWrapperType.value == I2CWrapperType.MockI2CWrapper.value:
            return None  # TODO: return the moch Chen
        else:
            raise Exception('Unrecognized provider type - cannot create provider')
