# from __future__ import print_function
import xml.etree.ElementTree as ET
from Marvell.EMClientPy.src.ElectricalManagmentModels.Board_Model import Board


def XML_Parser(path=None, text=None):
    """
    Allows only one and exactly one parameter - either path or explicit text
    :param path: the path to a file
    :param text: the text of the xml
    :return: DB generated from xml data
    """
    if path is not None and text is None:
        root = ET.parse(path).getroot()
    elif path is None and text is not None:
        root = ET.fromstring(text)
    else:
        raise Exception('XML_Parser method allows only one and exactly one parameter')

    b = Board(root)
    return b


