from enum import IntEnum

NULL = 0


# generic return codes
GT_ERROR           = 0xFFFFFFFF
GT_OK              = 0x00   # Operation succeeded
GT_FAIL            = 0x01   # Operation failed
GT_BAD_VALUE       = 0x02   # Illegal value
GT_OUT_OF_RANGE    = 0x03   # Value is out of range
GT_BAD_PARAM       = 0x04   # Illegal parameter in function called
GT_BAD_PTR         = 0x05   # Illegal pointer value
GT_BAD_SIZE        = 0x06   # Illegal size
GT_BAD_STATE       = 0x07   # Illegal state of state machine
GT_SET_ERROR       = 0x08   # Set operation failed
GT_GET_ERROR       = 0x09   # Get operation failed
GT_CREATE_ERROR    = 0x0A   # Fail while creating an item
GT_NOT_FOUND       = 0x0B   # Item not found
GT_NO_MORE         = 0x0C   # No more items found
GT_NO_SUCH         = 0x0D   # No such item
GT_TIMEOUT         = 0x0E   # Time Out
GT_NO_CHANGE       = 0x0F   # The parameter is already in this value
GT_NOT_SUPPORTED   = 0x10   # This request is not support
GT_NOT_IMPLEMENTED = 0x11   # This request is not implemented
GT_NOT_INITIALIZED = 0x12   # The item is not initialized
GT_NO_RESOURCE     = 0x13   # Resource not available (memory ...)
GT_FULL            = 0x14   # Item is full (Queue or table etc...)
GT_EMPTY           = 0x15   # Item is empty (Queue or table etc...
GT_INIT_ERROR      = 0x16   # Error occurred while INIT process
GT_NOT_READY       = 0x1A   # The other side is not ready yet
GT_ALREADY_EXIST   = 0x1B   # Tried to create existing item
GT_OUT_OF_CPU_MEM  = 0x1C   # Cpu memory allocation failed.
GT_ABORTED         = 0x1D   # Operation has been aborted.
GT_MAX_STATUS      = 0x1E   # Maximum number of error including GT_ERROR
