from Marvell.PyMIBS.Common.XMLPaths import xmlPathsData

ROOT = xmlPathsData['root']

def debug(msg):
    return f"[Debug]  {msg}"

def RetElement(addr, offset, value):
    return {
        "addr": addr,
        "offset": offset,
        "value": value
    }

def getEM_XML_Path(deviceName: str):
    try:
        return xmlPathsData['EM'][deviceName.upper()]
    except:
        return None

def getCMIS_XML_Path(deviceName: str):
    try:
        return xmlPathsData['CMIS'][deviceName.upper()]
    except:
        return None

