from Marvell.PySub20.PySub20 import PySub20Provider


prov : PySub20Provider = PySub20Provider()
prov.setLog(1)
prov.Connect(0x5498)
prov.SetI2CConfigParams(0x5498)
data = prov.I2C_Read(0x70, 0, 0, 1)
print(data)
rc = prov.I2C_Write(0x70, 0, 0, [data[0] + 1], 1)
print(rc)
data = prov.I2C_Read(0x70, 0, 0, 1)
print(data)
