from builtins import object
import platform
from .TCClient import *
from .OvenAPI import *
from .FlexMaxTCAPI import *
from .EnvisysChamber import *
from .TCClientMock import *
from .enumOvenType import OvenTypeEnum as Type

class TCClientFactory(object):

    @staticmethod
    def getTCInstance(type=Type.THERMOTRON, isMock: bool = False):
        
        if isMock:
            m_tc = TCClientMock()
        else:
            flex_api = FlexMaxTCAPI()
            oven_api = OvenAPI(type)
            m_tc = TCClient(flex_api, oven_api)

        return m_tc
