from Marvell.SafetyMonitor.ABCSafetyAdapter import ABCSafetyAdapter


class TCFlexMaxAdapter(ABCSafetyAdapter):
    def __init__(self, tc_client):
        self.tc_client = tc_client


    def read_current_value(self):
        return self.tc_client.TempControlReadFlexTCTemperature(None)


    def set_safe_value(self, safe_val):
        return self.tc_client.TempControlSetFlexTCTemperature(None, safe_val, 15)


