class Table2Entry(object):
    def __init__(self):
        self.col1 = ""
        self.col2 = ""
        self.col3 = ""
        self.col3 = ""
        self.col4 = ""


class Table2(object):
    def __init__(self):
        self.entries = [Table2Entry]
        self.entries.pop()

    def __getitem__(self, item):
        return self.entries[item]

    def __iter__(self):
        for entry in self.entries:
            yield entry


