from typing import Union

from .model import IfcsError
from .hws_model import HwsError

class IfcsException(Exception):
    def __init__(self, error: Union[IfcsError, HwsError]):
        self.context = error.detail.context
        self.reason = error.detail.reason
        self.desc = error.detail.desc
