from typing import Any, List, Optional

from pydantic import BaseModel

from . import ifcs_model


class IfcsNodeInfo(BaseModel):
    node_count: int


class IfcsNodeStats(BaseModel):
    ids: List[int]
    counters: List[int]


class IfcsDevportAttrs(BaseModel):
    devport: int
    attrs: List[ifcs_model.ifcs_attr_t]


class IfcsDevportStats(BaseModel):
    devport: int
    ids: List[int]
    counters: List[int]


class IfcsDevportStatsDetail(BaseModel):
    devport: int
    ids: List[int]
    counters: List[ifcs_model.ifcs_counter_t]


class IfcsErrorDetail(BaseModel):
    reason: int
    desc: str
    context: dict


class IfcsError(BaseModel):
    detail: IfcsErrorDetail


class IfcsVersion(BaseModel):
    version: str
    release_date: str
    build_date: str
    node_count: Optional[int] = None
